-- View structure for view_daerah_desa
-- ----------------------------
DROP VIEW IF EXISTS `view_daerah_desa`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_daerah_desa` AS select `mp`.`kode_daerah` AS `kode_daerah`,(select `mp1`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp1` where (left(`mp`.`kode_daerah`,2) = `mp1`.`kode_daerah`)) AS `nm_propinsi`,(select `mp2`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp2` where (left(`mp`.`kode_daerah`,5) = `mp2`.`kode_daerah`)) AS `nm_kabupaten`,(select `mp3`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp3` where (left(`mp`.`kode_daerah`,8) = `mp3`.`kode_daerah`)) AS `nm_kecamatan`,`mp`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp` where (`mp`.`kategori` = 'desa');

-- ----------------------------
-- View structure for view_daerah_kabupaten
-- ----------------------------
DROP VIEW IF EXISTS `view_daerah_kabupaten`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_daerah_kabupaten` AS select `mp`.`kode_daerah` AS `kode_daerah`,(select `mp1`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp1` where (left(`mp`.`kode_daerah`,2) = `mp1`.`kode_daerah`)) AS `nm_propinsi`,(select sum(`k1`.`luas_kawasan`) AS `sum(k1.luas_kawasan)` from `mp_kumuh` `k1` where (left(`k1`.`kode_daerah`,5) = `mp`.`kode_daerah`) group by left(`k1`.`kode_daerah`,5)) AS `jml_luas_kumuh`,`mp`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp` where (`mp`.`kategori` = 'kabupaten / kota');

-- ----------------------------
