DROP VIEW IF EXISTS `view_daerah_kecamatan`;
CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `view_daerah_kecamatan` AS select `mp`.`kode_daerah` AS `kode_daerah`,(select `mp1`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp1` where (left(`mp`.`kode_daerah`,2) = `mp1`.`kode_daerah`)) AS `nm_propinsi`,(select `mp2`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp2` where (left(`mp`.`kode_daerah`,5) = `mp2`.`kode_daerah`)) AS `nm_kabupaten`,`mp`.`nm_daerah` AS `nm_daerah` from `mp_daerah` `mp` where (`mp`.`kategori` = 'kecamatan');

